import tkinterclass app:    def __init__(self):        self.r = tkinter.Tk()                self.v = tkinter.IntVar()        b1 = tkinter.Radiobutton(self.r, text = 'First', value = 1, variable = self.v)        b1.grid(row = 0, column = 0, sticky = tkinter.W)        b2 = tkinter.Radiobutton(self.r, text = 'Second', value = 2, variable = self.v)        b2.grid(row = 1, column = 0, sticky = tkinter.W)        b3 = tkinter.Radiobutton(self.r, text = 'Third', value = 3, variable = self.v)        b3.grid(row = 2, column = 0, sticky = tkinter.W)    def start(self):        self.r.mainloop()        if __name__ == '__main__':    app().start()